const { generateScissorHash, randomNumber } = require('../../helper/mainHelper');
const socketManager = require('../manager/SocketManager');
const scissorsController = require('../controller/ScissorsController');
const { v4: uuidv4 } = require('uuid');

//0: rock, 1: scissors, 2: paper

exports.getScissorsResult = async (data, socket) => {
    try {
        let { playerNumber, userId, betAmount, coinType } = data;
        if (playerNumber === 3) {
            playerNumber = randomNumber(3);
        }
        const seedData = await scissorsController.getSeedData(userId);
        const roundNumber = uuidv4();
        const dealerNumber = scissorsWinner(seedData.serverSeedData.seed, seedData.clientSeedData.seed, roundNumber);
        const result = checkWinner(playerNumber, dealerNumber);
        const response = await scissorsController.saveScissorsRound({ userId, betAmount, playerNumber, dealerNumber, result, coinType, roundNumber, clientSeed: seedData.clientSeedData.seed, serverSeed: seedData.serverSeedData.seed });
        socketManager.sendBetResult({ playerNumber, dealerNumber, winResult: result, result: response }, socket);
        if (response.status) {
            setTimeout(() => {
                socketManager.sendBetHistory({
                    userId: response.roundData.userId,
                    gameType: 'scissor',
                    roundNumber: response.roundData.roundNumber,
                    betAmount: response.roundData.betAmount,
                    coinType: response.roundData.coinType,
                    payout: response.roundData.payout,
                    roundResult: response.roundData.roundResult,
                    roundState: true
                });
                socketManager.sendNewRoundData(response);
            }, 7000);
        }
    }
    catch (err) {
        console.error({ title: 'ScissorsRound => getScissorsResult', message: err.message });
    }
}

exports.getHistory = async (data, socket) => {
    try {
        const { userId } = data;
        const response = await scissorsController.getHistory({ userId });
        socketManager.sendHistoryData({ history: response }, socket);
    }
    catch (err) {
        console.error({ title: 'ScissorsRound => getHistory', message: err.message });
    }
}

const scissorsWinner = (serverSeed, clientSeed, roundNumber) => {
    const hash = generateScissorHash(serverSeed, clientSeed, roundNumber);
    const value = parseInt(hash[0], 16);
    let winScissors;
    if (value <= 5)
        winScissors = 0;
    else if (value > 5 && value <= 10)
        winScissors = 1;
    else
        winScissors = 2;
    return winScissors;
}

const checkWinner = (player, dealer) => {
    switch (player) {
        case 0:
            if (dealer === 1) return 'win';
            else if (dealer === 2) return 'lost';
            else return 'draw';
        case 1:
            if (dealer === 2) return 'win';
            else if (dealer === 0) return 'lost';
            else return 'draw';
        case 2:
            if (dealer === 0) return 'win';
            else if (dealer === 1) return 'lost';
            else return 'draw';
        default:
            return '';
    };
}

import * as PIXI from "pixi.js";
import { Resource } from "./data/Resource";
import { SlotLayer } from "./layer/SlotLayer";
import SlotSocketManager from "./utils/SlotSocketManager";

export class SlotApp extends PIXI.Application {

    FRAME_RATE      = 30;

    canvas_width    = 0;
    canvas_height   = 0;

    layer_slot      = null;

    frame_speed     = 0;
    frame_count     = 0;
    speed_delta     = 0;

    authData        = null;
    currency        = null;
    betAmount       = 0;
    betLines        = 0;
    autoCount       = 0;

    started         = false;

    constructor(option) {
        super(option);
    }

    onResize(width, height) {
        if(this.canvas_width === width && this.canvas_height === height)
            return;

        this.canvas_width = width;
        this.canvas_height = height;

        if(this.layer_slot !== null)
            this.layer_slot.onResize(width, height);

        this.renderer.resize(width, height);
    }

    startGame() {
        this.frame_speed = 60 / this.FRAME_RATE;

        this.layer_slot = new SlotLayer();
        this.stage.addChild(this.layer_slot);

        const keys = [];
        Resource.forEach(resource => {
            PIXI.Assets.add(resource.key, resource.src);
            keys.push(resource.key);
        });

        PIXI.Assets.load(keys, (progress) => {

        })
        .then((textures) => {
            this.started = true;

            if(this.layer_slot !== null)
                this.layer_slot.init(textures);

            this.ticker.add((delta) => {
                if(this.layer_slot !== null)
                    this.layer_slot.updateAnimation();
            });

            this.ticker.add((delta) => {
                if(this.layer_slot !== null)
                    this.layer_slot.updateTweening();
            });
        });
    }

    destroy(bRemoveView, bStageOptions) {
        super.destroy(bRemoveView, bStageOptions);
    }

    bet() {
        if(this.authData === null || !this.authData.isAuth) 
            return;

        if(this.autoCount < 0) {
            if(this.layer_slot !== null)
                this.layer_slot.running = false;
            return;
        }

        this.updateLoading(true);

        this.autoCount--;

        const request = {
            userId: this.authData.userData._id,
            betAmount: this.betAmount,
            lines: this.betLines,
            coinType: this.currency
        }

        SlotSocketManager.getInstance().joinBet(request);
    }

    showResult(roundData, rewardData) {
        if(this.layer_slot !== null)
            this.layer_slot.showResult(roundData, rewardData);
    }

    updateCurrency(currency) {
        this.currency = currency;
    }

    updateBetAmount(betAmount) {
        this.betAmount = betAmount;
    }

    updateBetLines(lines) {
        this.betLines = lines;
    }

    updateAutoCount(autoCount) {
        this.autoCount = autoCount;
    }

    updateAuthData(authData) {
        this.authData = authData;
    }

    updateGameState() {
        this.postMessage({type: 'playzelo-Slot-UpdateGameState', data: ''});
    }

    updateLoading(flag) {
        this.postMessage({type: 'playzelo-Slot-UpdateLoading', data: flag});
    }

    postMessage(message) {
        window.postMessage(message, '*');
    }

    // render(delta) {
    //     if(!this.started)
    //         return;

    //     this.speed_delta += delta;
    //     this.frame_count = 0;

    //     while(this.speed_delta >= this.frame_speed) {
    //         this.speed_delta -= this.frame_speed;
    //         this.frame_count++;
    //     }

    //     if(this.frame_count === 0)
    //         return;

    //     super.render();
    // }
}

