@layer base {
  * {
    @apply border-border;
  }

  html {
    /* Prevent zoom on input focus for mobile */
    touch-action: manipulation;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    /* Safe area padding for mobile devices */
    padding-top: env(safe-area-inset-top);
    padding-bottom: env(safe-area-inset-bottom);
    padding-left: env(safe-area-inset-left);
    padding-right: env(safe-area-inset-right);
    /* Prevent overscroll bounce */
    overscroll-behavior: none;
    /* Smooth scrolling */
    scroll-behavior: smooth;
    -webkit-overflow-scrolling: touch;
  }

  /* Hide scrollbars but keep functionality */
  .hide-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }
}

@layer utilities {
  /* Hide ugly search cancel button in Chrome */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  .no-default-hover-elevate {}
  .no-default-active-elevate {}

  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: -1;
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  .border.toggle-elevate::before {
    inset: -1px;
  }

  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    border-radius: inherit;
    z-index: 999;
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after {
    inset: -1px;
  }

  /* Game-specific utilities */
  .game-glow-win {
    box-shadow: 0 0 20px hsl(var(--win) / 0.4), 0 0 40px hsl(var(--win) / 0.2);
  }

  .game-glow-loss {
    box-shadow: 0 0 20px hsl(var(--loss) / 0.4), 0 0 40px hsl(var(--loss) / 0.2);
  }

  .game-glow-active {
    box-shadow: 0 0 15px hsl(var(--bet-active) / 0.4), 0 0 30px hsl(var(--bet-active) / 0.2);
  }

  /* Crash game graph gradient */
  .crash-gradient {
    background: linear-gradient(180deg, 
      hsl(var(--win) / 0.1) 0%, 
      transparent 100%
    );
  }

  /* Pulse animation for active elements */
  @keyframes pulse-glow {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.6;
    }
  }

  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  /* Number counter animation */
  @keyframes count-up {
    from {
      transform: translateY(100%);
      opacity: 0;
    }
    to {
      transform: translateY(0);
      opacity: 1;
    }
  }

  .animate-count-up {
    animation: count-up 0.3s ease-out;
  }

  /* Slot machine spin effect */
  @keyframes slot-spin {
    0% {
      transform: translateY(0);
    }
    100% {
      transform: translateY(-100%);
    }
  }

  /* Crash line animation */
  @keyframes crash-line {
    from {
      stroke-dashoffset: 1000;
    }
    to {
      stroke-dashoffset: 0;
    }
  }

  /* Mine reveal animation */
  @keyframes mine-reveal {
    0% {
      transform: scale(0.8) rotateY(0deg);
      opacity: 0;
    }
    50% {
      transform: scale(1.1) rotateY(90deg);
    }
    100% {
      transform: scale(1) rotateY(180deg);
      opacity: 1;
    }
  }

  .animate-mine-reveal {
    animation: mine-reveal 0.4s ease-out forwards;
  }

  /* Plinko ball drop */
  @keyframes plinko-drop {
    0% {
      transform: translateY(-20px);
      opacity: 0;
    }
    100% {
      transform: translateY(0);
      opacity: 1;
    }
  }

  /* Touch-friendly tap highlight */
  .tap-highlight {
    -webkit-tap-highlight-color: hsl(var(--primary) / 0.1);
  }

  /* Min touch target */
  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Hide scrollbar but keep functionality */
  .hide-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }
}

