    },
    TabButton: {
        fontSize: '16px',
        lineHeight: '24px',
        padding: '36px 0px 8px',
        marginLeft: '36px',
        color: '#9893cb',
        background: 'transparent',
        border: 'none',
        outline: 'none',
        cursor: 'pointer',
        fontWeight: 'bold',
        textTransform: 'uppercase'
    },
    TabButtonActive: {
        color: '#f0ecff',
        borderBottom: '2px solid #f0ecff',
        borderRadius: '4px 4px 0 0'
    },
    HistoryDataBox: {
        width: '100%',
        minHeight: '750px'
    },
    HistoryTable: {
        width: '100%'
    },
    HistoryTableHeader: {
        fontSize: '12px',
        lineHeight: '16px',
        fontWeight: '400',
        padding: '24px 40px 8px',
        color: '#9893cb',
        zIndex: '10'
    },
    HistoryTableBody: {
        padding: '12px 24px',
        color: '#9893cb',
        textAlign: 'center'
    },
    HistoryStateBox: {
        border: '1px solid #363646',
        borderRadius: '8px',
