    const today = new Date();
    today.setHours(0, 0, 0, 0);
    return Array.from(this.users.values())
      .filter(u => u.lastSeenAt && u.lastSeenAt >= today)
      .sort((a, b) => (b.lastSeenAt?.getTime() || 0) - (a.lastSeenAt?.getTime() || 0));
