const TurtleManagement = () => {
    const classes = useStyles();
    const { showLoading, hideLoading } = useContext(LoadingContext);
    const userAuth = useSelector((state) => state.userAuth);
    const dispatch = useDispatch();

    const [order, setOrder] = useState('asc');
    const [orderBy, setOrderBy] = useState('createdAt');
    const [data, setData] = useState([]);

    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [page, setPage] = useState(0);
    const [totalRows, setTotalRows] = useState(0);

    useEffect(() => {
        init()
        // eslint-disable-next-line
    }, [dispatch, rowsPerPage, page]);

    const init = async () => {
        showLoading();
        const userId = userAuth?.userData?._id;
        const response = await loadDashBoardData({ id: userId });
        if (response.status) {
            setTotalRows(Number(response.turtleCount));
            const requestData = {
                id: userId,
                offset: page,
                count: rowsPerPage
            }
            const response1 = await getTurtleData(requestData);
            if (response1.status) {
                setData(response1.turtleData);
            }
        }
        hideLoading();
    };

    const handleRequestSort = (property) => {
        let tempProperty = property;
        const isAsc = orderBy === tempProperty && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(tempProperty);
    };

    const handlePageChange = (value) => {
        setPage(value - 1)
    };

    const changePerPage = (value) => {
        setRowsPerPage(value);
    };

    const handleDetail = () => {
        dispatch({ type: 'SET_MENU_PATH', data: 'turtle-detail' });
    };

    const CircleItem = ({ position }) => {
        let color = position === 0 ? '#ff9314' : position === 1 ? '#f23068' : '#0d42ff'
        return (
            <Box style={{ width: `15px`, height: `15px`, background: `${color}`, borderRadius: '50%' }}></Box>
        );
    };

    return (
        <Box className={classes.PlayerContainer}>
            <Box className={classes.TableTitleBox}>
                <span>All Turtlerace Rounds</span>
            </Box>
            <Box className={classes.TableMainBox}>
                <Table>
                    <EnhancedTableHead
                        order={order}
                        orderBy={orderBy}
                        onRequestSort={handleRequestSort}
                    />
                    <TableBody>
                        {
                            data?.map((item, index) => (
                                <TableRow key={index} className={classes.TableRow}>
                                    <TableCell>
                                        {item._id}
                                    </TableCell>
                                    <TableCell>
                                        {item.roundNumber}
                                    </TableCell>
                                    <TableCell>
                                        {new Intl.DateTimeFormat('en-US').format(new Date(item.roundDate))}
                                    </TableCell>
                                    <TableCell>
                                        <Box className={classes.WinnerCell}>
                                            <CircleItem position={item.winnerInfo.findIndex((info) => info === 0)} />
                                            <CircleItem position={item.winnerInfo.findIndex((info) => info === 1)} />
                                            <CircleItem position={item.winnerInfo.findIndex((info) => info === 2)} />
                                        </Box>
                                    </TableCell>
                                    <TableCell>
                                        {item.serverSeed}
                                    </TableCell>
                                    <TableCell className={classes.ActionCell}>
                                        <Link to={`/games/turtle-detail?id=${item._id}`}>
                                            <IconButton onClick={handleDetail} color="primary" aria-label="upload picture" component="span" className={classes.ActionButton}>
                                                <Visibility />
                                            </IconButton>
                                        </Link>
                                    </TableCell>
                                </TableRow>
                            ))
                        }
                    </TableBody>
                </Table>
                <TablePagination
                    component="div"
                    count={totalRows}
                    onPageChange={(e, v) => handlePageChange(v + 1)}
                    onRowsPerPageChange={(e) => changePerPage(e.target.value)}
                    page={page}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[10, 25, 50]}
                />
            </Box>
        </Box>
    );
};

export default TurtleManagement;

