const mongoose = require('mongoose');

const ModelSchema = mongoose.Schema({
    currencyName: { type: String, default: '' },
    decimal: { type: Number, default: 4 },
    fullName: { type: String, default: '' },
    token: { type: String, default: '' },
    available: { type: Boolean, default: true },
    withdrawable: { type: Boolean, default: true },
    swapable: { type: Boolean, default: true },
    regDate: { type: Date, default: new Date() }
}, { autoIndex: true, timestamps: true });

ModelSchema.set('toObject', { virtuals: true });
ModelSchema.set('toJSON', { virtuals: true });

module.exports = mongoose.model('CurrencyList', ModelSchema);

const preState = {
    isAuth: false,
    userData: null,
    balanceData: {}
};

const authReducer = (state = preState, action) => {
    switch (action.type) {
        case 'SET_AUTH':
            return {
                ...state,
                isAuth: true
            };
        case 'SET_USERDATA':
            return {
                ...state,
                userData: action.data
            };
        case 'SET_BALANCEDATA':
            return {
                ...state,
                balanceData: action.data
            };
        default:
            state = { ...state };
            break;
    }
    return state;
};

export default authReducer;

