const MenuModal = ({ open, setOpen }) => {
    const classes = useStyles();
    const dispatch = useDispatch();

    const { addToast } = useToasts();
    const { showLoading, hideLoading } = useContext(LoadingContext);

    const menuOption = useSelector((state) => state.menuOption);
    const [menuItems, setMenuItems] = useState([
        {
            url: '/app/home',
            text: 'Home',
            icon: HomeIcon,
            width: '34px',
            height: '27px'
        },
        {
            url: '/app/games',
            text: 'Games',
            icon: GamesIcon,
            width: '34px',
            height: '25px',
            child: [
                {
                    url: '/scissor',
                    text: 'Scissor',
                    icon: SRPIcon,
                    width: '22px',
                    height: '31px',
                    available: false
                },
                {
                    url: '/turtlerace',
                    text: 'Turtlerace',
                    icon: TurtleRaceIcon,
                    width: '32px',
                    height: '31px',
                    available: false
                },
                {
                    url: '/mines',
                    text: 'Mines',
                    icon: MinesIcon,
                    width: '28px',
                    height: '28px',
                    available: false
                },
                {
                    url: '/dice',
                    text: 'Dice',
                    icon: DiceIcon,
                    width: '28px',
                    height: '28px',
                    available: false
                }
            ]
        },
        {
            url: '/app/bonues',
            text: 'Bonus',
            icon: BonusIcon,
            width: '25px',
            height: '23px'
        },
        {
            url: '/app/tournaments',
            text: 'Tournaments',
            icon: TournamentIcon,
            width: '28px',
            height: '25px'
        },
        {
            url: '/app/affiliate',
            text: 'Affiliate',
            icon: AffiliateIcon,
            width: '28px',
            height: '28px'
        },
        {
            url: '/app/help',
            text: 'Help Center',
            icon: HelpIcon,
            width: '35px',
            height: '35px'
        }
    ]);

    useEffect(() => {
        const path = window.location.pathname;
        dispatch({ type: 'SET_MENUROUTER', data: path });
        if (path === '/') window.location.assign("/app/home");
        getGameListData();
        // eslint-disable-next-line
    }, []);

    const handleMenuRouter = (path) => {
        dispatch({ type: 'SET_MENUROUTER', data: path });
        handleClose();
    };

    const handleClose = () => setOpen(false);

    const getGameListData = async () => {
        showLoading();
        const response = await getAvailableGames();
        if (response.status) {
            let menuData = [...menuItems];
            // eslint-disable-next-line
            menuData[1].child.map((child, index) => {
                let position = response.data.findIndex(item => item.gameName === child.text);
                if (position >= 0) {
                    menuData[1].child[index].available = true;
                }
            });
            setMenuItems([...menuData]);
        }
        else {
            addToast(response.message, { appearance: 'error', autoDismiss: true });
        }
        hideLoading();
    };

    return (
        <Modal
            open={open}
            onClose={handleClose}
            slotProps={{ backdrop: { className: classes.backdrop } }}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
        >
            <Box className={classes.ModalBox}>
                <IconButton className={classes.ModalCloseButton} onClick={handleClose}>
                    <Close />
                </IconButton>
                <Box className={classes.ModalBodyBox}>
                    <Box className={classes.GamesLayout}>
                        <span className={classes.GameBoxTitle}>Games</span>
                        <Box className={classes.GamesBox}>
                            {
                                menuItems[1].child.map((item, index) => (
                                    <Link
                                        key={index}
                                        className={clsx(classes.MenuItemLink, classes.GameMenu)}
                                        to={menuItems[1].url + item.url}
                                        onClick={() => handleMenuRouter(menuItems[1].url + item.url)}
                                    >
                                        <Box className={classes.GameItem}>
                                            <img src={item.icon} alt="icon" />
                                        </Box>
                                    </Link>
                                ))
                            }
                        </Box>
                    </Box>
                    <Box className={clsx(classes.MenuBox, classes.MenuOpen)}>
                        <ul className={classes.MenuItems}>
                            <div>
                                {
                                    menuItems.map((item, index) => (
                                        <li
                                            key={index}
                                            className={
                                                menuOption.menuRouter === item.url ? clsx(classes.MenuItem, classes.ActiveMenu)
                                                    : classes.MenuItem
                                            }
                                        >
                                            <Link className={classes.MenuItemLink} to={item.url} onClick={() => handleMenuRouter(item.url)}>
                                                <img src={item.icon} alt="icon" width={item.width} height={item.height} />
                                                {
                                                    !menuOption.menuVisible && <span>{item.text}</span>
                                                }
                                                {
                                                    item.child &&
                                                    <KeyboardArrowRight />
                                                }
                                            </Link>
                                        </li>
                                    ))
                                }
                            </div>
                        </ul>
                        <Box className={classes.BugBox}>
                            <Box>
                                <img src={ReportBugIcon} alt="icon" />
                                REPORT A BUG
                            </Box>
                            <span>Up to <label>$10,000</label> bounty program</span>
                        </Box>
                    </Box>
                </Box>
            </Box>
        </Modal>
    );
};

MenuModal.propTypes = {
    open: PropTypes.bool.isRequired,
    setOpen: PropTypes.func.isRequired
};

export default MenuModal;

const io = require('socket.io-client');
const { DEV_MDOE, MANAGEMENT_OPTION } = require('../../config');

module.exports = class ManageSocket {
    socket = null;

    constructor() {
        this.socket = io.connect(DEV_MDOE ? `http://127.0.0.1:${MANAGEMENT_OPTION.port}` : `https://management.memewarsx.com:4001`);
        this.bind();
    }

    bind() {
        this.socket.on('connect', () => {
        });

        this.socket.on('disconnect', () => {
        });
    }

    userBalanceUpdated(data) {
        if (this.socket === null)
            return;

        this.socket.emit('balanceUpdated', data);
    }

    updateWargerAmount(data) {
        if (this.socket === null)
            return;

        this.socket.emit('updateWargerAmount', data);
    }

    newBetHistory(data) {
        this.socket.emit('newBetHistory', data);
    }
}

