const Tier1Card = ({ userData }) => {
    const classes = useStyles();
    const currency = { coinType: 'ZELO' };

    const claimCommission = 0.3;
    const [claimAmount, setClaimAmount] = useState(0);

    useEffect(() => {
        let amount = 0;
        // eslint-disable-next-line
        userData.map((data) => {
            let wargerData = data.wargerData;
            if (wargerData.length > 0) {
                let totalWarger = wargerData[0].totalWargerAmount;
                let claimedAmount = Number(wargerData[0].campaignClaimAmount ? wargerData[0].campaignClaimAmount : 0);
                let availableAmount = (totalWarger - claimedAmount) / 100 * claimCommission;
                if (availableAmount > 0) amount += availableAmount;
            }
        });
        setClaimAmount(amount);
        // eslint-disable-next-line
    }, [userData]);

    return (
        <Box className={classes.CardLayout}>
            <Box className={classes.CardTitleBox}>
                <img className={classes.CardTitleIcon} src={TournamentsIcon} alt="icon" />
                <span className={classes.CardTitleText}>Tier 1 Commission</span>
            </Box>
            <Box className={classes.AmountBox}>
                <img className={classes.CurrencyIcon} src={`/assets/images/coins/${currency?.coinType?.toLowerCase()}.png`} alt={currency?.coinType} />
                <span className={classes.AmountText}>{claimAmount.toFixed(4)}</span>
            </Box>
            <Typography className={classes.ClaimText}>Total Wagered</Typography>
            <Box className={classes.CommissionTextBox}>
                <Typography className={classes.CommissionText}>Your total commission earnings from players on the first tier</Typography>
            </Box>
        </Box>
    );
};

export default Tier1Card;

exports.createApp = (config) => {
    const express = require('express');
    const cors = require('cors');
    const compression = require('compression');
    const cookieParser = require('cookie-parser');
    const cookieSession = require('cookie-session');
    const errorHandler = require('errorhandler');
    const fileUpload = require('express-fileupload');

    const app = express();
    app.use(cors());
    app.use(express.json());
    app.use(express.urlencoded({ extended: true }));
    app.use(compression());
    app.use(cookieParser());
    app.use(fileUpload());
    app.use(cookieSession({
        name: 'session',
        keys: ['roostercookie'],
        maxAge: 24 * 60 * 60 * 1000
    }));

    const models = require('../models/index');
    models.mongoose.connect(config.DB)
        .then(() => {
            console.log('server connected to mongodb successfully');
        })
        .catch((err) => {
            console.error({ title: 'mongodb connection error', message: err.message });
            process.exit();
        });

    if (app.get('env') === 'development')
        app.use(errorHandler({ dumpExceptions: true, showStack: true }));
    else
        app.use(errorHandler());

    app.set('port', config.serverInfo.port);
    return app;
};

