const DepositBonusCard = ({ cardNumber, minDeposit, percents, active = false }) => {
    const classes = useStyles();
    const dispatch = useDispatch();

    const getCardTitle = () => {
        if (cardNumber === 1) return '1st';
        else if (cardNumber === 2) return '2nd';
        else if (cardNumber === 3) return '3rd';
        else return '4th';
    };

    const handleDeposit = () => {
        if (active)
            dispatch({ type: 'SET_WALLET_MODAL', data: true });
    };

    return (
        <Box className={clsx(classes.CardBox, active ? '' : classes.DeactiveCard)}>
            <Box className={classes.CardTitle}><span>{getCardTitle()}</span> Deposit</Box>
            <Box className={classes.UptoBox}>Up to</Box>
            <Box className={classes.PercentBox}>{percents[2]}%</Box>
            <Box className={classes.MinDepositBox}>
                <span>Min deposit:</span>
                <Box>
                    {minDeposit}
                </Box>
            </Box>
            <Button className={classes.DepositButton} onClick={handleDeposit}>Deposit now</Button>
        </Box>
    );
};

export default DepositBonusCard;

import { io } from 'socket.io-client';
import Config from "config/index";

const baseInit = async () => {
    Config.Root.socket = io(Config.Root.socketServerUrl, { transports: ['websocket'] });
    console.log("websockect connected");
}

export default baseInit;

const dataManager = require('../manager/DataManager');

module.exports = class ChatSocket {
    socket = null;

    constructor(server) {
        this.socket = require('socket.io')({
            cors: {
                origin: '*',
                method: ['GET', 'POST']
            }
        }).listen(server);
        this.bind();
    }

    bind() {
        this.socket.on('connection', (client) => {
            console.log(`*** Socket ${client.id} connected! ***`);

            client.on('reconnect', (request) => {
                dataManager.addUserSocket(request.userId, client.id);
            });

            client.on('disconnect', () => {
                console.log(`### Socket ${client.id} disconnected ###`);
                dataManager.removeUserSocket(client.id);
            });

            client.on('getChatData', (callback) => {
                dataManager.getChatData(callback);
            });

            client.on('sendNewChat', (request) => {
                dataManager.newChat(request, client.id);
            });
        });
    }

    broadCast(packetName, packetData = null) {
        this.socket.emit(packetName, packetData);
    }

    sendTo(socket, packetName, packetData = null) {
        this.socket.to(socket).emit(packetName, packetData);
    }
}

