const PlinkoManagement = () => {
    const classes = useStyles();
    const { showLoading, hideLoading } = useContext(LoadingContext);
    const userAuth = useSelector((state) => state.userAuth);
    const dispatch = useDispatch();

    const [order, setOrder] = useState('asc');
    const [orderBy, setOrderBy] = useState('createdAt');
    const [data, setData] = useState([]);

    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [page, setPage] = useState(0);
    const [totalRows, setTotalRows] = useState(0);

    useEffect(() => {
        init()
        // eslint-disable-next-line
    }, [dispatch, rowsPerPage, page]);

    const init = async () => {
        showLoading();
        const userId = userAuth?.userData?._id;
        const response = await loadDashBoardData({ id: userId });
        if (response.status) {
            setTotalRows(Number(response.plinkoCount));
            const requestData = {
                id: userId,
                offset: page,
                count: rowsPerPage
            }
            const response1 = await getPlinkoData(requestData);
            if (response1.status) {
                setData(response1.diceData);
            }
        }
        hideLoading();
    };

    const handleRequestSort = (property) => {
        let tempProperty = property;
        const isAsc = orderBy === tempProperty && order === 'asc';
        setOrder(isAsc ? 'desc' : 'asc');
        setOrderBy(tempProperty);
    };

    const handlePageChange = (value) => {
        setPage(value - 1)
    };

    const changePerPage = (value) => {
        setRowsPerPage(value);
    };

    return (
        <Box className={classes.PlayerContainer}>
            <Box className={classes.TableTitleBox}>
                All Plinko Rounds
            </Box>
            <Box className={classes.TableMainBox}>
                <Table>
                    <EnhancedTableHead
                        order={order}
                        orderBy={orderBy}
                        onRequestSort={handleRequestSort}
                    />
                    <TableBody>
                        {
                            data?.map((item) => (
                                <TableRow key={item._id} className={classes.TableRow}>
                                    <TableCell>
                                        {item.roundNumber}
                                    </TableCell>
                                    <TableCell>
                                        {new Intl.DateTimeFormat('en-US').format(new Date(item.roundDate))}
                                    </TableCell>
                                    <TableCell>
                                        {item.userData.userNickName}
                                    </TableCell>
                                    <TableCell>
                                        {Number(item.betAmount).toFixed(4)}
                                    </TableCell>
                                    <TableCell>
                                        {item.coinType.coinType}
                                    </TableCell>
                                    <TableCell style={{ textTransform: 'capitalize' }}>
                                        {item.payout > 1 ? 'Win' : item.payout === 1 ? 'Draw' : 'Lost'}
                                    </TableCell>
                                    <TableCell>
                                        {item.clientSeed}
                                    </TableCell>
                                    <TableCell>
                                        {item.serverSeed}
                                    </TableCell>
                                    <TableCell>
                                        {item.payout}
                                    </TableCell>
                                </TableRow>
                            ))
                        }
                    </TableBody>
                </Table>
                <TablePagination
                    component="div"
                    count={totalRows}
                    onPageChange={(e, v) => handlePageChange(v + 1)}
                    onRowsPerPageChange={(e) => changePerPage(e.target.value)}
                    page={page}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[10, 25, 50]}
                />
            </Box>
        </Box>
    );
};

export default PlinkoManagement;

import Config from "config/index";
import { io } from "socket.io-client";

export default class MinesSocketManager {
    static _instance = null;

    socket;
    authData;

    static getInstance() {
        if (MinesSocketManager._instance === null)
            MinesSocketManager._instance = new MinesSocketManager();

        return MinesSocketManager._instance;
    }

    connect(authData) {
        this.socket = io(Config.Root.minesSocketUrl, { transports: ['websocket'] });
        this.authData = authData;
        let self = this;

        this.socket.on('connect', function () {
            self.reconnect();
        });

        this.socket.on('joinBetResult', function (response) {
            const message = { type: 'playzelo-mines-joinBetResult', data: response };
            self.postMessage(message);
        });

        this.socket.on('pickCellResult', function (response) {
            const message = { type: 'playzelo-mines-pickCellResult', data: response };
            self.postMessage(message);
        });

        this.socket.on('roundResult', function (response) {
            const message = { type: 'playzelo-mines-roundResult', data: response };
            self.postMessage(message);
        });

        this.socket.on('activeRoundResult', function (response) {
            const message = { type: 'playzelo-mines-activeRoundResult', data: response };
            self.postMessage(message);
        });
    }

    postMessage(message) {
        window.postMessage(message, '*');
    }

    updateAuthData(data) {
        this.authData = data;
    }

    disconnect() {
        this.socket.disconnect();
    }

    reconnect() {
        if (this.authData.isAuth)
            this.socket.emit('reconnect', this.authData?.userData?._id);
    }

    joinBet(data) {
        this.socket.emit('join_bet', data)
    }

    finishBet(data) {
        this.socket.emit('finish_bet', data)
    }

    pickCell(data) {
        this.socket.emit('pick_cell', data);
    }

    getActiveRound(data) {
        this.socket.emit('active_round', data);
    }
}

