exports.createApp = (config) => {
    const express = require('express');
    const cors = require('cors');
    const compression = require('compression');
    const cookieParser = require('cookie-parser');
    const cookieSession = require('cookie-session');
    const errorHandler = require('errorhandler');
    const fileUpload = require('express-fileupload');

    const app = express();
    app.use(cors());
    app.use(express.json());
    app.use(express.urlencoded({ extended: true }));
    app.use(compression());
    app.use(cookieParser());
    app.use(fileUpload());
    app.use(cookieSession({
        name: 'session',
        keys: ['roostercookie'],
        maxAge: 24 * 60 * 60 * 1000
    }));

    const models = require('../models/index');
    models.mongoose.connect(config.DB)
        .then(() => {
            console.log('server connected to mongodb successfully');
        })
        .catch((err) => {
            console.error({ title: 'mongodb connection error', message: err.message });
            process.exit();
        });

    if (app.get('env') === 'development')
        app.use(errorHandler({ dumpExceptions: true, showStack: true }));
    else
        app.use(errorHandler());

    app.set('port', config.serverInfo.port);
    return app;
};

const models = require('../../models/index');
const mongoose = require('mongoose');

exports.saveNewChat = async (data) => {
    try {
        const { userId, text, type } = data;
        const result = await new models.userChatModel({
            userId: mongoose.Types.ObjectId(userId),
            chatText: text,
            chatType: type,
            date: new Date()
        }).save();
        const chatData = await models.userChatModel.aggregate([
            {
                $lookup: {
                    from: 'users',
                    foreignField: '_id',
                    localField: 'userId',
                    as: 'userData'
                }
            },
            {
                $unwind: '$userData'
            },
            {
                $match: {
                    _id: result._id
                }
            },
            {
                $project: {
                    name: '$userData.userNickName',
                    userId: '$userId',
                    text: '$chatText',
                    type: '$chatType',
                    date: '$date',
                    avatar: 'avatar1.png'
                }
            }
        ])
        return { status: true, data: chatData };
    }
    catch (err) {
        console.error({ title: 'ChatController => saveNewChat', message: err.message });
        return { status: false, message: err.message };
    }
}

exports.getChatData = async () => {
    try {
        const result = await models.userChatModel.aggregate([
            {
                $lookup: {
                    from: 'users',
                    foreignField: '_id',
                    localField: 'userId',
                    as: 'userData'
                }
            },
            {
                $unwind: '$userData'
            },
            {
                $sort: {
                    date: -1
                }
            },
            {
                $limit: 50
            },
            {
                $project: {
                    name: '$userData.userNickName',
                    userId: '$userId',
                    text: '$chatText',
                    type: '$chatType',
                    date: '$date',
                    avatar: 'avatar1.png'
                }
            }
        ])
        return { status: true, data: result };
    }
    catch (err) {
        console.error({ title: 'ChatController => getChatData', message: err.message });
        return { status: false, message: err.message };
    }
}

