const MinesRound = require('./MinesRound');
const MinesController = require('../controller/MinesController');
const SocketManager = require('../manager/SocketManager');
const { RoundResult } = require('../constant');

var AllMines = [];

const getRoundData = (data) => {
    try {
        let { userId } = data;
        let round = null;
        AllMines.map((minesRound) => {
            if (minesRound.isOwner(userId) && !minesRound.getFinished()) {
                round = minesRound;
            }
        });
        return round;
    }
    catch (err) {
        console.error({ title: 'RoundManager => getRoundData', message: err.message });
    }
}

const removeRound = async (data) => {
    try {
        let { userId, type } = data;
        let index = AllMines.findIndex((minesRound) => minesRound.isOwner(userId));
        if (index >= 0) {
            const roundData = AllMines[index];
            const result = await MinesController.saveMinesRound(roundData);
            if (result.status) {
                SocketManager.sendBetHistory({
                    userId: result.data.userId,
                    gameType: 'mines',
                    roundNumber: result.data.roundNumber,
                    betAmount: result.data.betAmount,
                    coinType: result.data.coinType,
                    payout: result.data.payout,
                    roundResult: result.data.roundResult,
                    roundState: true
                });
            }

            let balanceData;
            if (roundData.roundResult === RoundResult.payout || roundData.roundResult === RoundResult.finish) {
                let amount = Number(Number(roundData.betAmount * roundData.currentPayout).toFixed(6));
                const response = await MinesController.updateMyBalance({ userId: roundData.userId, coinType: roundData.coinType, betAmount: -amount, type });
                if (response.status)
                    balanceData = response.data;
            }

            AllMines.splice(index, 1);
            return { status: true, balanceData };
        }
    }
    catch (err) {
        console.error({ title: 'RoundManager => removeRound', message: err.message });
        return { status: false };
    }
}

exports.getMinesRound = async (data, socket) => {
    try {
        let { userId, betAmount, coinType, minesCount } = data;
        const roundData = getRoundData({ userId });
        if (roundData !== null) {
            SocketManager.sendBetResult({ status: true, currentPayout: roundData.currentPayout, nextPayout: roundData.nextPayout }, socket);
        }
        else {
            let seedData = await MinesController.getSeedData(userId);
            const balanceResult = await MinesController.updateMyBalance(data);
            if (balanceResult.status) {
                let minesData = new MinesRound({ userId, betAmount, coinType, minesCount, clientSeed: seedData.clientSeedData.seed, serverSeed: seedData.serverSeedData.seed });
                AllMines.push(minesData);
                SocketManager.sendBetResult({ status: true, currentPayout: minesData.currentPayout, nextPayout: minesData.nextPayout, balanceData: balanceResult.data }, socket);
            }
            else {
                SocketManager.sendBetResult({ status: false, message: balanceResult.message }, socket);
            }
        }
    }
    catch (err) {
        console.error({ title: 'RoundManager => getMinesRound', message: err.message });
    }
}

exports.pickCell = async (data, socket) => {
    try {
        const { userId, i, j } = data;
        const round = getRoundData({ userId });
        if (round !== null && !round.getFinished()) {
            const response = round.pickCell(i, j);
            SocketManager.sendPickCellResult({ ...response, i, j, currentPayout: round.currentPayout, nextPayout: round.nextPayout, diamondCount: round.diamondCount }, socket);
            if (response.status && response.info) {
                round.lostRound();
                await removeRound({ userId });
                SocketManager.sendRoundResult(round, socket);
            }
        }
    }
    catch (err) {
        console.error({ title: 'RoundManager => pickCell', message: err.message });
    }
}

exports.getActiveRound = (data, socket) => {
    try {
        const { userId } = data;
        const roundData = getRoundData({ userId });
        if (roundData !== null) {
            SocketManager.activeRoundResult({ status: true, roundData }, socket);
        }
        else {
            SocketManager.activeRoundResult({ status: false }, socket);
        }
    }
    catch (err) {
        console.error({ title: 'RoundManager => getActiveRound', message: err.message });
    }
}

exports.finishRound = async (data, socket) => {
    try {
        const { userId } = data;
        const round = getRoundData({ userId });
        let response;
        if (round.getCellPicked()) {
            round.payoutRound();
            response = await removeRound({ userId });
        }
        else {
            round.finishRound();
            response = await removeRound({ userId, type: 'finish' });
        }
        if (response.status)
            SocketManager.sendRoundResult({ ...round, balanceData: response.balanceData }, socket);
        else
            SocketManager.sendRoundResult({ ...round }, socket);
    }
    catch (err) {
        console.error({ title: 'RoundManager => pickCell', message: err.message });
    }
}

const constant = require('../constant');
const { v4: uuidv4 } = require('uuid');
const { randomNumber, generateTurtleHash } = require('../../helper/mainHelper');
const socketManager = require('../manager/SocketManager');
const dataManager = require('../manager/DataManager');
const turtleController = require('../controllers/TurtleController');

TURTLE_YELLOW = 0;
TURTLE_RED = 1;
TURTLE_BLUE = 2;

module.exports = class TurtleRaceRound {
    countDownTime
    countDownInterval

    roundNumber
    roundState
    roundDate
    roundStartTime

    winnerInfo
    betUsers
    serverSeed

    constructor() {
        this.countDownTime = constant.turtleraceInfo.countDown.time;

        this.roundState = constant.round.state.countDown;
        this.roundNumber = uuidv4();
        this.setServerSeed();
        this.roundDate = new Date();

        this.betUsers = new Array();

        let self = this;
        this.countDownInterval = setInterval(() => {
            this.countDown(response => {
                if (response.state === 'start') {
                    self.startRound();
                }
                else if (response.state === 'count') {
                    self.countDownRound(response.count);
                }
            });
        }, constant.turtleraceInfo.countDown.interval * 1000);
    }

    setServerSeed = async () => {
        let seedData = await turtleController.getSeedData();
        this.serverSeed = seedData.serverSeedData.seed;
    }

    currentRound = async (socket) => {
        const historyData = await turtleController.getLastRounds();
        const betHistoryData = await turtleController.getBetHistory();
        let response = { history: historyData, betHistory: betHistoryData };
        if (this.roundState === constant.round.state.started)
            response['stateData'] = {
                state: this.roundState,
                startTime: this.roundStartTime,
                winnerInfo: this.winnerInfo
            };
        else
            response['stateData'] = {
                state: this.roundState
            };
        socketManager.currentRoundResult(response, socket);
    }

    countDown = (callback) => {
        if (this.countDownTime < 0.0) {
            clearInterval(this.countDownInterval);
            this.roundState = constant.round.state.started;
            callback({ state: 'start' });
        }
        else {
            callback({ state: 'count', count: this.countDownTime.toFixed(2) });
            this.countDownTime -= constant.turtleraceInfo.countDown.interval;
        }
    }

    winnerLogic() {
        let betAmount = 0;
        this.betUsers.map((betUser) => {
            betAmount += betUser.betAmount
        });

        const hash = generateTurtleHash(this.serverSeed, this.roundNumber, betAmount);
        const value = parseInt(hash[0], 16);
        let winTurtle;
        if (value <= 5)
            winTurtle = 0;
        else if (value > 5 && value <= 10)
            winTurtle = 1;
        else
            winTurtle = 2;

        let resultTurtle = [];
        while (resultTurtle.length < constant.turtleraceInfo.turtleCount) {
            let number = randomNumber(constant.turtleraceInfo.turtleCount)
            let index = resultTurtle.findIndex((item) => item === number)
            if (index < 0) resultTurtle.push(number);
        }

        let topIndex = resultTurtle.findIndex((item) => item === 0);
        resultTurtle[topIndex] = resultTurtle[winTurtle];
        resultTurtle[winTurtle] = 0;
        return resultTurtle;
    }

    async addBetUser(data, socket) {
        if (this.roundState === constant.round.state.countDown) {
            if (!this.checkBetUser(data)) {
                let betUser = {
                    userId: data.userId,
                    betAmount: data.betAmount,
                    coinType: data.coinType,
                    xFactor: constant.turtleraceInfo.xFactor,
                    profit: 0,
                    turtleNum: data.turtleNum,
                    isWin: false,
                    userNickName: ''
                };
                const response = await turtleController.updateMyBalance({ userId: data.userId, balance: data.betAmount, coinType: data.coinType });
                if (response.status) {
                    betUser.userNickName = response.userData.userNickName;
                    this.betUsers.push(betUser);
                }
                socketManager.joinBetResult({ state: true, data: response }, socket);
                socketManager.newBetUser({ betUser });
            }
        }
        else {
            socketManager.joinBetResult({ state: false, data: data }, socket);
        }
    }

    async removeBetUser(data, socket) {
        let cancelUser;
        this.betUsers = this.betUsers.filter(item => {
            if (item.userId !== data.userId) {
                return true;
            }
            else {
                cancelUser = item;
                return false;
            }
        });
        const response = await turtleController.updateMyBalance({ userId: cancelUser.userId, balance: -cancelUser.betAmount, coinType: cancelUser.coinType });
        socketManager.cancelBetResult({ state: true, data: response }, socket);
        socketManager.removeBetUser({ cancelUser });
    }

    checkBetUser(data) {
        this.betUsers.map((item) => {
            if (item.userId === data.userId)
                return true
        });
        return false;
    }

    countDownRound(count) {
        socketManager.sendRoundCountDown(count);
    }

    startRound() {
        let self = this;
        this.winnerInfo = this.winnerLogic();
        socketManager.sendRoundStart(this.winnerInfo);
        this.roundStartTime = new Date();

        let topWinner = this.winnerInfo.findIndex((info) => info === 0);
        this.betUsers.map((betUser) => {
            if (betUser.turtleNum === topWinner) {
                betUser.isWin = true;
                betUser.profit = betUser.betAmount * betUser.xFactor;
            }
        });

        setTimeout(() => {
            turtleController.saveTurtleRound({ roundNumber: this.roundNumber, winnerInfo: this.winnerInfo, roundDate: this.roundDate, betUsers: this.betUsers, serverSeed: this.serverSeed })
                .then((result) => {
                    if (result) {
                        let historyData = [];
                        result.map((item) => {
                            const history = {
                                userId: item.betUserId,
                                gameType: 'turtle',
                                roundNumber: this.roundNumber,
                                betAmount: item.betAmount,
                                coinType: item.coinType,
                                payout: item.xFactor,
                                roundResult: item.isWin ? 'win' : 'lost',
                                roundState: true
                            };
                            historyData.push(history);
                        });
                        socketManager.sendBetHistory(historyData);
                    }

                    self.roundState = constant.round.state.finished;
                    socketManager.sendRoundStop(self.winnerInfo);
                    turtleController.updateBalances(self.betUsers)
                        .then(() => {
                            socketManager.balanceUpdated();
                        })
                        .catch((err) => {
                            console.error({ title: 'startRound => updateBalances', message: err.message });
                        });
                    setTimeout(() => {
                        socketManager.sendRoundFinished();
                        dataManager.createRound();
                    }, constant.turtleraceInfo.completeRound.time * 1000);
                })
                .catch((err) => {
                    console.error({ title: 'startRound => saveTurtleRound', message: err.message });
                });
        }, constant.turtleraceInfo.runRound.time * 1000);
    }
}

import * as PIXI from "pixi.js";
import { gameApp } from "../index";
const { BaseLayer } = require("./BaseLayer");
const { PATTERN_RULE } = require('../data/Constant');

export class SlotLayer extends BaseLayer {

    REEL_COUNT          = 5;
    REEL_WIDTH          = 134.0;
    REEL_HEIGHT         = 444.0;
    REEL_OFFSET_X       = 143.0;
    REEL_OFFSET_Y       = 148.0;

    SYMBOL_COUNT        = 11;
    SYMBOL_WIDTH        = 112.0;
    SYMBOL_HEIGHT       = 118.0;

    MOVE_SYMBOLS        = 30;

    slot_textures       = null;
    coin_textures       = null;

    spr_platform        = null;
    spr_logo            = null;
    graphic_line        = null;
    container_total     = null;
    container_rule      = null;
    container_result    = null;
    container_freespin  = null;
    container_reel      = null;

    reels               = [];
    targets             = [0, 0, 0, 0, 0];
    tweening            = [];
    results             = null;
    rewards             = null;

    running             = false;
    freespin_mode       = false;
    freespin_count      = 0;

    reward_index        = 0;
    total_payout        = 0;
    round_payout        = 0;

    init(resource) {
        super.init(resource);

        this.coin_textures = [
            resource.coin_bnb,
            resource.coin_btc,
            resource.coin_eth,
            resource.coin_trx,
            resource.coin_usdt,
            resource.coin_zelo
        ];

        this.spr_platform = this.addChild(this.createSprite(resource.platform, this.GAME_WIDTH / 2, this.GAME_HEIGHT / 2 + 31.0, 0.5, 0.5));
        this.spr_logo = this.addChild(this.createSprite(resource.logo, this.GAME_WIDTH / 2, this.GAME_HEIGHT / 2 - 275.5, 0.5, 0.5));

        this.createSlot(resource);
        this.createGraphic();
        this.createTotalPayout(resource);
        this.createRulePayout(resource);
        this.createResult(resource);
        this.createFreeSpin(resource);
        this.updateMask();
    }

    onResize(width, height) {
        // const scale_x = width / this.GAME_WIDTH;
        const scale_y = height / this.GAME_HEIGHT;
        // const scale = Math.min(scale_x, scale_y);
        const scale = scale_y;

        this.scale.set(scale);
        this.x = (width - this.GAME_WIDTH * scale) / 2;
        this.y = (height - this.GAME_HEIGHT * scale) / 2;

        this.updateMask();
    }

    updateTweening() {
        const now = Date.now();
        const remove = [];

        for(let i = 0; i < this.tweening.length; i++) {
            const t = this.tweening[i];
            const phase = Math.min(1, (now - t.start) / t.time);
            t.object[t.property] = this.lerp(t.propertyBeginValue, t.target, t.easing(phase)); 

            if(t.change)
                t.change(t);

            if(phase === 1) {
                t.object[t.property] = t.target;
                if(t.complete)
                    t.complete(t);
                remove.push(t);
            }
        }

        for(let i = 0; i < remove.length; i++) 
            this.tweening.splice(this.tweening.indexOf(remove[i]), 1);
    }

    updateAnimation() {
        for(let i = 0; i < this.reels.length; i++) {
            const r = this.reels[i];
            r.blur.blurY = (r.position - r.prevPosition) * 30;
            r.prevPosition = r.position;

            for(let j = 0; j < r.symbols.length; j++) {
                const s = r.symbols[j];
                const prevy = s.y;
                s.y = (((r.position + j) % r.symbols.length) - 0.5) * this.REEL_OFFSET_Y;
                if(s.y < 0 && prevy > this.REEL_OFFSET_Y) {
                    if(Math.floor(r.position) >= (this.targets[i] - 3) && Math.floor(r.position) < this.targets[i]) {
                        let result = this.getResult();
                        let symbol_index = result[this.targets[i] - Math.floor(r.position) - 1][i];
                        s.texture = this.slot_textures[symbol_index];
                    }
                    else {
                        if(this.freespin_mode)
                            s.texture = this.slot_textures[Math.floor(Math.random() * this.slot_textures.length - 1)];
                        else
                            s.texture = this.slot_textures[Math.floor(Math.random() * this.slot_textures.length)];
                    }

                    s.x = this.REEL_WIDTH / 2;
                    s.anchor.set(0.5, 0.5);
                }
            }
        }
    }

    createSlot(resource) {
        this.slot_textures = [
            resource.slot_1,
            resource.slot_2,
            resource.slot_3,
            resource.slot_4,
            resource.slot_5,
            resource.slot_6,
            resource.slot_7,
            resource.slot_8,
            resource.slot_9,
            resource.slot_wild,
            resource.slot_scatter
