const Header = () => {
    const classes = useStyles();
    const dispatch = useDispatch();

    const menuOption = useSelector((state) => state.menuOption);
    const userAuth = useSelector((state) => state.userAuth);

    const [menuEl, setMenuEl] = useState(null);
    const menuOpen = Boolean(menuEl);

    const handleMenu = (event) => {
        setMenuEl(event.currentTarget);
    };

    const handleCloseMenu = () => {
        setMenuEl(null);
    };

    const handleMenuCollaps = () => {
        dispatch({ type: 'SET_MENU_COLLAPS', data: !menuOption.menuCollaps });
    };

    const handleChangePass = () => {
        handleCloseMenu();
        dispatch({ type: 'SET_MENU_PATH', data: '/change-password' });
    }

    const handleLogout = () => {
        handleCloseMenu();
        localStorage.clear();
        window.location.reload();
    }

    return (
        <header className={classes.HeaderContainer}>
            <Link to='/' className={!menuOption.menuCollaps ? classes.LogoButton : clsx(classes.LogoButton, 'w50')}>
                <span>{!menuOption.menuCollaps ? 'PLAYZELO' : 'PZ'}</span>
            </Link>
            <nav className={!menuOption.menuCollaps ? clsx(classes.NavBox, 'navbar') : clsx(classes.NavBox, 'navbar', 'ml-50')}>
                <IconButton className={classes.MenuButton} onClick={handleMenuCollaps}>
                    <MenuRounded />
                </IconButton>
                <Box className={classes.ProfileBox}>
                    <Avatar src={`assets/images/default.png`} className={classes.PlayerAvatar} />
                    <Button
                        id="menu-button"
                        aria-controls={menuOpen ? 'menu-list' : undefined}
                        aria-haspopup="true"
                        aria-expanded={menuOpen ? 'true' : undefined}
                        onClick={handleMenu}
                        className={classes.MenuButton}
                    >
                        {userAuth?.userData?.username}
                    </Button>
                    <Menu
                        id="menu-list"
                        anchorEl={menuEl}
                        open={menuOpen}
                        onClose={handleCloseMenu}
                        MenuListProps={{
                            'aria-labelledby': 'menu-button'
                        }}
                    >
                        <MenuItem><Box onClick={handleChangePass}><Link to='/change-password'>Change Password</Link></Box></MenuItem>
                        <Divider />
                        <MenuItem onClick={handleLogout}>Logout</MenuItem>
                    </Menu>
                </Box>
            </nav>
        </header>
    );
};

export default Header;

const DiceRound = require('../data/DiceRound');

var userSockets = [];

exports.addUserSocket = (userId, socket) => {
    if (!userId || this.getUserById(userId) !== null) return;

    userSockets.push({ userId, socket });
};

exports.removeUserSocket = (socket) => {
    const index = userSockets.findIndex(user => user.socket === socket);
    if (index >= 0) userSockets.splice(index, 1);
};

exports.joinBet = (data, socket) => {
    DiceRound.getDiceResult(data, socket);
};

exports.getHistory = (data, socket) => {
    DiceRound.getHistory(data, socket);
};

import axios from 'axios';
import ApiConfig from './baseConfig';

export default class PlayerService {
    constructor() {
        axios.interceptors.request.use(
            config => {
                config.baseURL = ApiConfig.Root.apiUrl
                config.headers = {
                    authorization: `Bearer ${this.getToken()}`
                }
                return config
            },
            error => Promise.reject(error)
        )
        axios.interceptors.response.use(
            response => {
                if (response.data.status === 'fail' && response.data.session) {
                    // eslint-disable-next-line
                    alert(response.data.message)
                    this.clearToken()
                    setTimeout(() => {
                        window.location.assign("/")
