      setNewPromoBonus("");
      setNewPromoMaxUses("");
      toast({ title: "Ð£ÑÐ¿ÐµÑˆÐ½Ð¾", description: "ÐŸÑ€Ð¾Ð¼Ð¾ÐºÐ¾Ð´ ÑÐ¾Ð·Ð´Ð°Ð½" });
    },
    onError: (error: any) => {
      toast({ title: "ÐžÑˆÐ¸Ð±ÐºÐ°", description: error.message, variant: "destructive" });
    },
  });

  const toggleAdminMutation = useMutation({
    mutationFn: async ({ odejs, isAdmin }: { odejs: string; isAdmin: boolean }) => {
